class ReadStateError extends Error {
    name = 'ReadStateError';
    constructor(message) {
        super(message || 'Unable to read state.');
    }
}
class ReadStorageError extends Error {
    name = 'ReadStorageError';
    constructor(message) {
        super(message || 'Unable to read storage.');
    }
}

export { ReadStateError, ReadStorageError };
